# PromptPath Icon Generator Script
# Run this script to create proper PNG icons

$icon16 = ""

$icon48 = ""

$icon128 = ""

[System.IO.File]::WriteAllBytes("$PSScriptRoot\icons\icon16.png", [System.Convert]::FromBase64String($icon16))
[System.IO.File]::WriteAllBytes("$PSScriptRoot\icons\icon48.png", [System.Convert]::FromBase64String($icon48))
[System.IO.File]::WriteAllBytes("$PSScriptRoot\icons\icon128.png", [System.Convert]::FromBase64String($icon128))

Write-Host "Icons created successfully!" -ForegroundColor Green
